using namespace std;

class TaskE {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, k;
		in >> n >> k;
		string add;
		for (int i: range(k)) {
			add += char(i + 'a');
		}
		string s;
		in >> s;
		for (char c: s) {
			size_t id = add.find(c);
			rotate(add.begin() + id, add.begin() + id + 1, add.end());
		}
		using Z = Zn<1000000007>;
		vector<Z> curans(26);
		Z cursum = Z::valueOf(1);
		for (char c: s) {
			Z curadd = cursum - curans[c - 'a'];
			curans[c - 'a'] += curadd;
			cursum += curadd;
		}
		for (int i: range(n)) {
			char c = add[i % k];
			Z curadd = cursum - curans[c - 'a'];
			curans[c - 'a'] += curadd;
			cursum += curadd;
		}
		out << cursum << "\n";

	}
};


int main() {
	std::ios_base::sync_with_stdio(false);
	TaskE solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(0);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}