using namespace std;

class TaskB {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, m;
		in >> n >> m;
		vector<vector<pair<int, bool>>> g(n);

		for (int i: range(m)) {
			int a, b;
			char c;
			in >> a >> b >> c;
			--a, --b;
			g[a].emplace_back(b, c == 'B');
			g[b].emplace_back(a, c == 'B');
		}

		function<int(int, int, vector<int>&)> dfs = [&](int v, int color, vector<int>& used) {
			if (used[v] != -1) {
				if (used[v] != color) {
					return 1000000;
				} else {
					return 0;
				};
			}
			used[v] = color;
			int res = color;
			for (auto to: g[v]) {
				res += dfs(to.first, color ^ to.second, used);
			}
			return min(res, 1000000);
		};
		int64_t ans = 1000000000;
		vector<int> usedAns;
		for (int colorIt = 0; colorIt < 2; ++colorIt) {
			std::cerr << "iteration" << std::endl;
			vector<int> used(n, -1);
			vector<int> used2(n, -1);
			vector<int> usedCur(n, -1);
			for (int i: range(n)) {
				for (auto& x: g[i]) {
					x.second = !x.second;
				}
			}

			int64_t curAns = 0;
			for (int i: range(n)) {
				if (-1 == used[i]) {
					int anus[2] = {dfs(i, 0, used), dfs(i, 1, used2)};
					if(anus[0] < anus[1]) {
						dfs(i, 0, usedCur);
					} else {
						dfs(i, 1, usedCur);
					}
					curAns += min(anus[0], anus[1]);
				}
			}
			if (curAns < ans) {
				usedAns = usedCur;
				ans = curAns;
			}
		}
		if (ans <= n) {
			out << ans << "\n";
			for (int i: range(n)) {
				if (usedAns[i]) {
					out << i + 1 << " ";
				}
			}
		} else {
			out << -1;
		}
	}
};


