using namespace std;

class TaskB {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, q;
		in >> n >> q;
		vector<vector<int>> child(n);
		for (int i: range(1, n)) {
			int a;
			in >> a;
			--a;
			child[a].push_back(i);
		}

		int time = 0;

		vector<int> ans(n);
		vector<int> size(n);

		function<map<int, int>(int)> dfs = [&](int v) {
			map<int, int> res;
			size[v] = 1;
			for (int to: child[v]) {
				auto z = dfs(to);
				size[v] += size[to];
				if (z.size() > res.size()) {
					z.swap(res);
				}

				for (auto p: z) {
					res[p.first] = p.second;
				}
			}

			res[size[v]] = v;
			int x = (size[v] + 1) / 2;
			ans[v] = res.lower_bound(x)->second;
			//cerr << "here" << v << ' ' <<size[v] << endl;
			//for (auto xx: res) {
			//	cerr << xx.first << ' ' << xx.second << endl;
			//}
			//cerr << "ans " << ans[v] << endl;
			return res;
		};


		dfs(0);

		for (int i: range(q)) {
			int x;
			in >> x;
			--x;
			out << ans[x] + 1 << "\n";
		}

	}
};


int main() {
	std::ios_base::sync_with_stdio(false);
	TaskB solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(0);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}