array<array<array<array<short, 10>, 10>, 1000>, 1000> sparse;

class TaskD {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n = 1000, m = 1000;
		in >> n >> m;
		auto v = make_vector<int>(n, m, 0);
		for (int i: range(n)) {
			for (int j: range(m)) {
				in >> v[i][j];
				//v[i][j] = (bool)(rand() % 5);
			}
		}
		vector<int> logs(1001);
		for (int i: inclusiveRange(2, 1000)) {
			logs[i] = logs[i - 1] + isPowerOf2(i);
		}
		auto square = make_vector<int>(n + 1, m + 1, 0);
		{
			auto right = make_vector<int>(n + 1, m + 1, 0);
			auto down = make_vector<int>(n + 1, m + 1, 0);
			for (int i: downrange(n)) {
				for (int j: downrange(m)) {
					if (v[i][j]) {
						right[i][j] = right[i][j + 1] + 1;
						down[i][j] = down[i + 1][j] + 1;
						square[i][j] = min(right[i][j], min(down[i][j], square[i + 1][j + 1] + 1));
					}
				}
			}
		}

		for (int i: downrange(n)) {
			for (int j: downrange(m)) {
				sparse[i][j][0][0] = (short) square[i][j];
				for (int q = 1; j + (1 << q) <= m; ++q) {
					int p = 0;
					sparse[i][j][p][q] = max(sparse[i][j][p][q - 1], sparse[i][j + (1 << (q - 1))][p][q - 1]);
				}
				for (int p = 1; i + (1 << p) <= n; ++p) {
					sparse[i][j][p][0] = max(sparse[i][j][p - 1][0], sparse[i + (1 << (p - 1))][j][p - 1][0]);
					for (int q = 1; j + (1 << q) <= m; ++q) {
						sparse[i][j][p][q] = max(sparse[i][j][p][q - 1], sparse[i][j + (1 << (q - 1))][p][q - 1]);
					}
				}
			}
		}
		//
		//for (int i: range(n)) {
		//	for(int j: range(m)) {
		//		out << square[i][j] << ' ';
		//	}
		//	out << "\n";
		//}
		//cerr << sparse[2][1][0][1] << endl;

		auto get = [&] (int lx, int ly, int rx, int ry) {
			int logx = logs[rx - lx];
			int logy = logs[ry - ly];
			int ans = 0;
			for (int x: {lx, rx - (1 << logx)}) {
				for (int y: {ly, ry - (1 << logy)}) {
					ans = max(ans, (int)sparse[x][y][logx][logy]);
				}
			}
			return ans;
		};

		int q = 1000000;
		in >> q;

		for (int _: range(q)) {
			int lx, ly, rx, ry;
			in >> lx >> ly >> rx >> ry;
			--lx, --ly;
			//lx = 0;
			//ly = 0;
			//rx = n;
			//ry = m;
			int l = 0;
			int r = min(ry - ly, rx - lx) + 1;
			while (r - l > 1) {
				int M = (l + r) / 2;
				if (get(lx, ly, rx - M + 1, ry - M + 1) >= M) {
					l = M;
				} else {
					r = M;
				}
			}

			out << l << "\n";
		}

	}
};


