using range_iterator_traits = std::iterator_traits<typename std::decay<decltype(std::declval<T>().begin())>::type>;


template <typename R>
auto prefixSums(const R& range) -> std::vector<typename range_iterator_traits<R>::value_type> {
	std::vector<typename range_iterator_traits<R>::value_type> result(
			static_cast<std::size_t>(
					std::distance(std::begin(range), std::end(range)) + 1
			)
	);
	std::partial_sum(std::begin(range), std::end(range), result.begin() + 1);
	return result;
}


using namespace std;

class TaskC {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n;
		in >> n;
		multiset<int64_t> sums;
		set<int> deleted;
		deleted.insert(-1);
		deleted.insert(n);

		vector<int64_t> v(n);
		for (int i: range(n)) {
			in >> v[i];
		}
		auto ps = prefixSums(v);
		sums.insert(ps.back());


		for (int i: range(n)) {
			int x;
			in >> x;
			--x;
			auto nxt = *deleted.lower_bound(x);
			auto prv = *--deleted.lower_bound(x) + 1;

			sums.erase(sums.find(ps[nxt] - ps[prv]));
			sums.insert(ps[nxt] - ps[x + 1]);
			sums.insert(ps[x] - ps[prv]);
			deleted.insert(x);

			out << *sums.rbegin() << "\n";
		}
	}
};


