#define int int64_t

class TaskC {
public:
	void solve(std::istream& in, std::ostream& out) {
		int n, m, k;
		in >> n >> m >> k;
		int N = 2 * n;
		int M = 2 * m;
		int g = gcd(N, M);
		int LCM = N * M / g;

		auto solve = [&](int x, int y) -> int64_t {
			//Nk - Ml = y - x
			if ((y - x) % g) {
				return numeric_limits<int64_t>::max();
			}
			int k, l;
			extendedGcd(N, M, k, l);
			k *= (y - x) / g;
			int64_t res = N * k + x;
			res %= LCM;
			if (res < 0) {
				res += LCM;
			}
			return res;
		};
		int z, u;
		for (int i: range(k)) {
			int x, y;
			in >> x >> y;
			int64_t ans = numeric_limits<int64_t>::max();
			for (int xx: {x, 2 * n - x}) {
				for (int yy: {y, 2 * m - y}) {
					auto tt = solve(xx, yy);
					if (tt == numeric_limits<int64_t>::max())
						continue;

					ans = min(ans, tt);
				}
			}

			if (ans == numeric_limits<int64_t>::max()) {
				out << -1;
			}
			else {
				out << ans;
			}
			out << "\n";
		}
	}
};

