using namespace std;

class TaskC {
public:
	vector<vector<int>> g;
	vector<vector<int>> colors;

	vector<int> anses;

	set<int> can_use;

	void dfs(int v, int p) {
		for (int c: colors[v]) {
			if (anses[c] != -1) {
				can_use.erase(anses[c]);
			}
		}
		for (int c: colors[v]) {
			if (anses[c] == -1) {
				anses[c] = *can_use.begin();
				can_use.erase(can_use.begin());
			}
		}

		for (int c: colors[v]) {
			can_use.insert(anses[c]);
		}

		for (int to: g[v]) {
			if (p == to) {
				continue;
			}
			dfs(to, v);
		}
	}
	void solve(std::istream& in, std::ostream& out) {
		int n, m;
		in >> n >> m;

		int ans = 1;
		g.resize(n);
		colors.resize(n);
		for (int i: range(n)) {
			int c;
			in >> c;
			updateMax(ans, c);
			colors[i].resize(c);
			for (int j: range(c)) {
				in >> colors[i][j];
				--colors[i][j];
			}
		}
		for (int i: range(n - 1)) {
			int a, b;
			in >> a >> b;
			--a, --b;
			g[a].push_back(b);
			g[b].push_back(a);
		}
		out << ans << "\n";
		anses.resize(m, -1);
		for (int i: range(ans)) {
			can_use.insert(i);
		}

		dfs(0, 0);

		for (int i: range(m)) {
			out << max(0, anses[i]) + 1 << " ";
		}
	}
};


