using namespace std;

class TaskB {
public:
	void solve(std::istream& in, std::ostream& out) {
		int64_t n, s;
		in >> n >> s;
		struct person {
			int64_t s, a, b;
		};
		vector<person> people(n);
		for (int i: range(n)) {
			in >> people[i].s >> people[i].a >> people[i].b;
		}

		int64_t total_slices = 0;
		int64_t total_a_slices = 0;
		int64_t total_b_slices = 0;
		for (int i: range(n)) {
			total_slices += people[i].s;
			if (people[i].a > people[i].b) {
				total_a_slices += people[i].s;
			} else {
				total_b_slices += people[i].s;
			}
		}

		int64_t total_pizzas = divideCeil(total_slices, s);
		int64_t total_a_pizzas = divideCeil(total_a_slices, s);
		int64_t total_b_pizzas = divideCeil(total_b_slices, s);

		int64_t extra = total_a_pizzas + total_b_pizzas - total_pizzas;
		assert(extra == 0 || extra == 1);
		sort(people, [](person l, person r) {
			return l.a - l.b > r.a - r.b;
		});

		auto solve = [&](int64_t a_pizzas, int64_t b_pizzas) -> int64_t {
			if (a_pizzas < 0 || b_pizzas < 0) {
				return 0;
			}
			int64_t a_slices = min(a_pizzas * s, total_a_slices);
			int64_t b_slices = min(b_pizzas * s, total_b_slices);
			if (a_slices < total_a_slices) {
				b_slices += total_a_slices - a_slices;
			} else {
				a_slices += total_b_slices - b_slices;
			}

			assert(a_slices + b_slices == total_slices);

			int64_t ans = 0;
			for (int i: range(n)) {
				int64_t cnt = min<int64_t>(a_slices, people[i].s);
				ans += people[i].a * cnt + people[i].b * (people[i].s - cnt);
				a_slices -= cnt;
			}
			return ans;
		};

		out << max(solve(total_a_pizzas, total_pizzas - total_a_pizzas), solve(total_pizzas - total_b_pizzas, total_b_pizzas));
	}
};


int main() {
	std::ios_base::sync_with_stdio(false);
	TaskB solver;
	std::istream& in(std::cin);
	std::ostream& out(std::cout);
	in.tie(nullptr);
	out << std::fixed;
	out.precision(20);
	solver.solve(in, out);
	return 0;
}