inline LL mod_mul(LL x,LL y,LL n)//return x*y%n
{
	LL d=(LL)( (long double)x*y/n );
	d=x*y-n*d;
	while (d<0) d+=n;
	while (d>=n) d-=n;
	return d;
}

inline LL powmod(LL x,LL t,LL base)
{
	LL res=1LL;
	while (t>0){
		if (t&1LL) res=mod_mul(res,x,base);
		x=mod_mul(x,x,base);
		t>>=1;
	}
	return res;
}

inline bool strong_pseudo_primetest(LL n,int base)
{
//	printf("testing: %lld %d\n",n,base);
	LL n2=n-1,res;
	int s=0;
	while (!(n2&1)) n2>>=1,++s;
	res=powmod(base,n2,n);
	if (res==1 || res==n-1) return 1;
	--s;
	while (s>=0){
		res=mod_mul(res,res,n);
		if (res==n-1) return 1;
		--s;
	}
	return 0;//n is not a strong pseudo prime
}

inline bool isPrime(LL n)
{
	if (n<2) return false;
	if (n<4) return true;
	if (strong_pseudo_primetest(n,2)==0) return false;
	if (strong_pseudo_primetest(n,3)==0) return false;
	if (n<1373653LL) return true;
	if (strong_pseudo_primetest(n,5)==0) return false;
	if (n<25326001LL) return true;
	if (strong_pseudo_primetest(n,7)==0) return false;
	if (n==3215031751LL) return false;
	if (n<25000000000LL) return true;
	if (strong_pseudo_primetest(n,11)==0) return false;
	if (n<2152302898747LL) return true;
	if (strong_pseudo_primetest(n,13)==0) return false;
	if (strong_pseudo_primetest(n,17)==0) return false;
	if (n<341550071728321LL) return true;
	if (strong_pseudo_primetest(n,19)==0) return false;
	if (strong_pseudo_primetest(n,23)==0) return false;
	if (strong_pseudo_primetest(n,29)==0) return false;
	if (strong_pseudo_primetest(n,31)==0) return false;
	if (strong_pseudo_primetest(n,37)==0) return false;
	return true;
}

int main()
{
	int n=0;
	for (int l=1;l<=300000000;l+=300000){
		int r=l+300000-1;
		L[n]=l;
		R[n]=r;
		++n;
	}
	int ans=0,a,b;
	scanf("%d%d",&a,&b);
	for (int i=0;i<n;++i){
		if (a<=L[i] && R[i]<=b){
			ans+=mem[i];
		}else{
			int l=max(L[i],a),r=min(R[i],b);
			int s=1,cnt=0;
			for (int i=1;s<=r;i+=2){
				int st=2;
				if (s<l){
					st=(int)sqrt(l-s);
					while (st>0 && s+(st-1)*(st-1)>=l) --st;
					while (s+st*st<l) ++st;
			
					if (st&1) ++st;
				}
				int s2=s+st*st;
				for (int j=st;s2<=r;j+=2){
					if (isPrime(s2)){
						++cnt;
					}
					s2+=(j<<2)+4;
				}
				s+=(i<<2)+4;
			}
			if (l<=2 && 2<=r) ++cnt;
			ans+=cnt;
		}
	}
	printf("%d\n",ans);
	return 0;
}