int a,b,c;

int main(){
	scanf("%d%d%d",&a,&b,&c);
	if(a-b+c>0&&a-b-c>0){
		puts("+");
	}else if(a-b+c==0&&a-b-c==0){
		puts("0");
	}else if(a-b+c<0&&a-b-c<0){
		puts("-");
	}else{
		puts("?");
	}
	return 0;
}