int n;
int a[200005],b[200005];
int whr[200005];

int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
	}
	for(int i=1;i<=n;i++){
		scanf("%d",b+i);
		if(b[i]){
			whr[b[i]]=i;
		}
	}
	int enx=b[n],enx2=b[n]-1,can=1;
	for(int i=n-1;i>=1;i--){
		if(!enx2){
			break;
		}
		if(b[i]!=enx2){
			can=0;
			break;
		}
		enx2--;
	}
	if(can){
		int can2=1;
		for(int i=enx+1;i<=n;i++){
			if(whr[i]-(i-enx-1)>0){
				can2=0;
				break;
			}
		}
		if(can2){
			printf("%d\n",n-enx);
			return 0;
		}
	}
	int maxx=0;
	for(int i=1;i<=n;i++){
		maxx=max(maxx,whr[i]-i+1);
	}
	printf("%d\n",maxx+n);
	return 0;
}