int n;
vector<int> nei[200005];
int jc[200005];
const int Mod=998244353;

int main(){
	jc[0]=1;
	for(int i=1;i<200005;i++){
		jc[i]=1LL*jc[i-1]*i%Mod;
	}
	scanf("%d",&n);
	for(int i=1;i<n;i++){
		int a,b;
		scanf("%d%d",&a,&b);
		nei[a].push_back(b);
		nei[b].push_back(a);
	}
	int ans=1;
	for(int i=1;i<=n;i++){
		int len=nei[i].size()-(i!=1);
		ans=1LL*ans*jc[len]%Mod*(i==1?1:len+1)%Mod;
	}
	ans=1LL*ans*n%Mod;
	printf("%d\n",ans);
	return 0;
}