long long n,m,k;

int main(){
	scanf("%I64d%I64d%I64d",&n,&m,&k);
	long long ans=0x3f3f3f3f3f3f3f3f;
	if(n/k+m/k==(n+m)/k){
		ans=0;
	}
	ans=min(ans,k-n%k);
	ans=min(ans,n%k);
	ans=min(ans,k-m%k);
	ans=min(ans,m%k);
	printf("%I64d %I64d\n",(n+m)/k,ans);
	return 0;
}