string s;
string ans="x";
int n;

int main(){
	cin>>n;
	cin>>s;
	int ff1=-1,ff2=-1;
	for(int i=1;i<n;i++){
		if(s[i]!='0'){
			if(i<(n+1)/2){
				ff1=i;
			}else{
				ff2=i;
				break;
			}
		}
	}
	string tmp;
	if(ff1!=-1){
		int jw=0;
		for(int i=ff1-1,j=n-1;j>=ff1;i--,j--){
			tmp=(char)((i>=0?s[i]:'0')+s[j]+jw-'0')+tmp;
			if(tmp[0]>'9'){
				tmp[0]-=10;
				jw=1;
			}else{
				jw=0;
			}
		}
		if(jw){
			tmp='1'+tmp;
		}
		ans=tmp;
	}
	if(ff2!=-1){
		int jw=0;
		tmp="";
		for(int i=ff2-1,j=n-1;i>=0;i--,j--){
			tmp=(char)((j>=ff2?s[j]:'0')+s[i]+jw-'0')+tmp;
			if(tmp[0]>'9'){
				tmp[0]-=10;
				jw=1;
			}else{
				jw=0;
			}
		}
		if(jw){
			tmp='1'+tmp;
		}
		if(ans=="x"||((ans.size()>tmp.size()||ans>tmp)&&ans.size()>=tmp.size())){
			ans=tmp;
		}
	}
	cout<<ans<<endl;
	return 0;
}