int n,m,k;
int s[2005][2005];

long long get(int a,int b){
	long long ans=(1LL*a*b-(a%n)*(b%m))/2;
	int x=(a/n)^(b/m);
	int ff=__builtin_popcount(x);
	if(ff%2){
		ans+=(a%n)*(b%m)-s[a%n][b%m];
	}else{
		ans+=s[a%n][b%m];
	}
	return ans;
}

int main(){
	scanf("%d%d%d",&n,&m,&k);
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			scanf("%1d",s[i]+j);
			s[i+n][j+m]=s[i][j];
			s[i][j+m]=s[i+n][j]=1-s[i][j];
		}
	}
	n*=2,m*=2;
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			s[i][j]+=s[i-1][j]+s[i][j-1]-s[i-1][j-1];
		}
	}
	while(k--){
		int a,b,c,d;
		scanf("%d%d%d%d",&a,&b,&c,&d);
		printf("%lld\n",get(c,d)-get(a-1,d)-get(c,b-1)+get(a-1,b-1));
	}
	return 0;
}