int n;
int a[100005],b[100005];
pair<long double,int> c[100005];
const long double Pi=3.141592653589793238462643383279;

int main(){
	scanf("%d",&n);
	for(int i=0;i<n;i++){
		scanf("%d%d",a+i,b+i);
		c[i]=make_pair(atan2(b[i],a[i]),i);
	}
	sort(c,c+n);
	long double minx=999;
	int mina,minb;
	for(int i=1;i<n;i++){
		if(c[i].first-c[i-1].first<=minx){
			minx=c[i].first-c[i-1].first;
			mina=c[i].second;
			minb=c[i-1].second;
		}
	}
	if(c[0].first+2*acos(-1)-c[n-1].first<minx){
		minx=c[0].first+2*acos(-1)-c[n-1].first;
		mina=c[0].second;
		minb=c[n-1].second;
	}
	printf("%d %d\n",mina+1,minb+1);
	return 0;
}