char s[200005],t[200005];
int cnt[27];
int n;

int main(){
	scanf("%s",s);
	n=strlen(s);
	for(int i=0;i<n;i++){
		cnt[s[i]-'a']++;
	}
	int fa=25;
	int ss=0;
	for(int i=0;i<26;i++){
		if(cnt[i]%2){
			while(cnt[fa]%2==0&&fa>i){
				fa--;
			}
			if(i==fa){
				cnt[i]--;
				t[n/2]=i+'a';
			}else{
				cnt[fa]--;
				cnt[i]++;
			}
		}
		for(int j=0;2*j<cnt[i];j++,ss++){
			t[ss]=t[n-ss-1]=i+'a';
		}
	}
	printf("%s\n",t);
	return 0;
}