int n;
int a[100005];

int main(){
	scanf("%d",&n);
	int cnt=0;
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
		cnt+=a[i];
	}
	sort(a+1,a+n+1);
	reverse(a+1,a+n+1);
	int A=cnt/n,B=cnt%n;
	int ans=0;
	for(int i=1;i<=n;i++){
		int to=A+(i<=B?1:0);
		ans+=abs(a[i]-to);
	}
	printf("%d\n",ans/2);
	return 0;
}