char s[1000005];
int pp[1000005];
vector<int> stk;
int n;

int main(){
	scanf("%s",s);
	n=strlen(s);
	for(int i=0;i<n;i++){
		if(s[i]=='<'||s[i]=='{'||s[i]=='['||s[i]=='('){
			stk.push_back(i);
		}else{
			if(!stk.size()){
				puts("Impossible");
				return 0;
			}
			int x=stk.back();
			pp[x]=i;
			stk.pop_back();
		}
	}
	if(stk.size()){
		puts("Impossible");
		return 0;
	}
	int ans=0;
	for(int i=0;i<n;i++){
		if(s[i]=='<'){
			if(s[pp[i]]!='>'){
				ans++;
			}
		}else if(s[i]=='{'){
			if(s[pp[i]]!='}'){
				ans++;
			}
		}else if(s[i]=='['){
			if(s[pp[i]]!=']'){
				ans++;
			}
		}else if(s[i]=='('){
			if(s[pp[i]]!=')'){
				ans++;
			}
		}
	}
	printf("%d\n",ans);
	return 0;
}