int n,m;
char s[1005][1005];
int num[1005][1005];
int dx[]={1,0,-1,0},dy[]={0,1,0,-1};
int cnt[1000005];

void bfs(int x,int y,int val){
	queue<pair<int,int> > q;
	q.emplace(x,y);
	num[x][y]=val;
	cnt[val]=1;
	while(q.size()){
		pair<int,int> X=q.front();
		q.pop();
		int x=X.first,y=X.second;
		for(int i=0;i<4;i++){
			int nx=x+dx[i],ny=y+dy[i];
			if(s[nx][ny]=='.'&&!num[nx][ny]){
				q.emplace(nx,ny);
				num[nx][ny]=val;
				cnt[val]++;
			}
		}
	}
}

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		scanf("%s",s[i]+1);
	}
	int xx=0;
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(s[i][j]=='.'&&!num[i][j]){
				bfs(i,j,++xx);
			}
		}
	}
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			if(s[i][j]=='.'){
				printf(".");
			}else{
				set<int> hv;
				int ans=1;
				for(int k=0;k<4;k++){
					int ni=i+dx[k],nj=j+dy[k];
					if(s[ni][nj]=='.'&&!hv.count(num[ni][nj])){
						ans+=cnt[num[ni][nj]];
						hv.insert(num[ni][nj]);
					}
				}
				printf("%d",ans%10);
			}
		}
		puts("");
	}
	return 0;
}