int n;
int a[300005];
map<int,int> lst;
int pre[300005];

int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
		pre[i]=lst[a[i]];
		lst[a[i]]=i;
	}
	int cnt=0;
	vector<pair<int,int> > ans;
	int frm=1;
	for(int i=2;i<=n;i++){
		if(pre[i]>=frm){
			cnt++;
			ans.emplace_back(frm,i);
			frm=i+1;
			i++;
		}
	}
	if(frm!=n+1){
		if(!ans.size()){
			puts("-1");
			return 0;
		}
		ans.back().second=n;
	}
	printf("%d\n",cnt);
	for(int i=0;i<ans.size();i++){
		printf("%d %d\n",ans[i].first,ans[i].second);
	}
	return 0;
}