int n,m;
int a[200005];
int vcl[200005];

int main(){
	scanf("%d%d",&n,&m);
	for(int i=1;i<=n;i++){
		scanf("%d",a+i);
		if(a[i]==a[i-1]){
			vcl[i]=vcl[i-1];
		}else{
			vcl[i]=i-1;
		}
	}
	while(m--){
		int l,r,x;
		scanf("%d%d%d",&l,&r,&x);
		if(a[r]!=x){
			printf("%d\n",r);
		}else if(vcl[r]>=l){
			printf("%d\n",vcl[r]);
		}else{
			puts("-1");
		}
	}
	return 0;
}