int n,k;
char s[100005];
char t[100005];

int main(){
	scanf("%d%d",&n,&k);
	scanf("%s",s+1);
	for(int i=1;i<=n;i++){
		if(!k){
			t[i]=s[i];
			continue;
		}
		int tox=max('z'-s[i],s[i]-'a');
		if(k>=tox){
			k-=tox;
			if('z'-s[i]==tox){
				t[i]='z';
			}else{
				t[i]='a';
			}
		}else{
			if(s[i]+k<='z'){
				t[i]=s[i]+k;
			}else{
				t[i]=s[i]-k;
			}
			k=0;
		}
	}
	if(k){
		puts("-1");
		return 0;
	}
	printf("%s\n",t+1);
	return 0;
}