int dp[55][55][55][55];
int n,m;
int s;
vector<pair<int,int> > nei[55];
int cnt[55];
int dist[55][55];

int CountCriminals(int now,int lst){
	int sum=cnt[now];
	for(auto i:nei[now]){
		if(i.first!=lst){
			sum+=CountCriminals(i.first,now);
		}
	}
	return sum;
}

int dfsdp(int s,int t,int all,int rem){
	int &ans=dp[s][t][all][rem];
	if(ans!=-1){
		return ans;
	}
	if(!rem){
		return ans=0;
	}
	if(nei[t].size()==1){
		return ans=(dfsdp(t,s,rem-all,rem-all)+nei[t][0].second);
	}
	int DP[55];
	memset(DP,0,sizeof(DP));
	DP[0]=0x3f3f3f3f;
	for(auto i:nei[t]){
		if(i.first!=s){
			for(int j=all;j>=1;j--){
				for(int k=1;k<=j;k++){
					DP[j]=max(DP[j],min(DP[j-k],dfsdp(t,i.first,k,rem)+dist[s][t]));
				}
			}
		}
	}
	return ans=DP[all];
}

int main(){
	scanf("%d",&n);
	for(int i=1;i<n;i++){
		int a,b,c;
		scanf("%d%d%d",&a,&b,&c);
		nei[a].emplace_back(b,c);
		nei[b].emplace_back(a,c);
		dist[a][b]=dist[b][a]=c;
	}
	scanf("%d%d",&s,&m);
	for(int i=0;i<m;i++){
		int xx;
		scanf("%d",&xx);
		cnt[xx]++;
	}
	int ans=0x3f3f3f3f;
	memset((int*)dp,-1,sizeof(dp));
	for(auto i:nei[s]){
		int all=CountCriminals(i.first,s);
		ans=min(ans,dfsdp(s,i.first,all,m));
	}
	printf("%d\n",ans);
	return 0;
}