int n,m;
const int Mod=1'000'000'007;
bool sushu[100005];
vector<int> hv;

int ksmii(int a,int b){
	if(!b){
		return 1;
	}
	int x=ksmii(a,b>>1);
	x=1LL*x*x%Mod;
	if(b&1){
		x=1LL*a*x%Mod;
	}
	return x;
}

int main(){
	scanf("%d%d",&n,&m);
	if(m%n){
		puts("0");
		return 0;
	}
	m/=n;
	int mm=m;
	for(int i=2;i<100005;i++){
		if(!sushu[i]){
			for(long long j=1LL*i*i;j<100005;j+=i){
				sushu[j]=1;
			}
			if(mm%i==0){
				hv.push_back(i);
				while(mm%i==0){
					mm/=i;
				}
			}
		}
	}
	if(mm!=1){
		hv.push_back(mm);
	}
	n=hv.size();
	int ans=0;
	for(int i=0;i<(1<<n);i++){
		int tochoo=1;
		for(int j=0;j<n;j++){
			if((i>>j)&1){
				tochoo*=hv[j];
			}
		}
		if(__builtin_popcount(i)%2){
			ans=(ans+Mod-ksmii(2,m/tochoo-1))%Mod;
		}else{
			ans=(ans+ksmii(2,m/tochoo-1))%Mod;
		}
	}
	printf("%d\n",ans);
	return 0;
}