#include <bits/stdc++.h>
using namespace std;

long long i, i1, j, k, t, n, res, a, m, b;
string s[5010];

int main() {
  ios::sync_with_stdio(0);
  cin.tie(0);

  //ifstream cin("input.txt");
  //ofstream cout("output.txt");

  cin >> t;
  for (i1 = 0; i1 < t; i1++) {
    cin >> n >> s[1];
    for (i = 2; i <= n; i++) {
      s[i] = "";
    }
    //cout << 1 << endl;
    for (i = 2; i <= n; i++) {
      for (j = 0; j < n - i + 1; j++) {
        s[i][j] = s[1][i + j - 1];
      }
      for (j = n - i + 1; j < n; j++) {
        if ((n - i) % 2 == 0) {
          s[i][j] = s[1][n - j - 1];
        } else {
          s[i][j] = s[1][i + j - n - 1];
        }
      }
    }
    for (i = 1; i <= n; i++) {
      //cout << s[i] << endl;
    }
    //cout << 4 << endl;
    res = 1;
    for (i = 2; i <= n; i++) {
      //cout << 5 << endl;
      if (s[i] < s[res]) {
        res = i;
      }
    }
    for (i = 0; i < n; i++) {
      cout << s[res][i];
    }
    cout << endl << res << endl;
  }

  return 0;
}