#include <bits/stdc++.h>
using namespace std;

#define endl "\n"
#define ll long long
#define INF (ll)1e18
#define maxn 500010
#define pb push_back

ll i, i1, j, k, k1, t[maxn], n, m, res, flag[10], a, b;
bool f[maxn];
vector<ll> adj[maxn];
vector<array<ll, 2>> sn;

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);

    //ifstream cin("input.txt");
    //ofstream cout("output.txt");

    cin >> n >> m;
    for (i = 0; i < m; i++) {
        cin >> a >> b;
        adj[a].pb(b); adj[b].pb(a);
    }
    for (i = 1; i <= n; i++) {
        cin >> t[i];
        sn.pb({t[i], i});
    }

    flag[2] = 1;
    for (i = 1; i <= n; i++) {
        if (adj[i].size() < t[i] - 1) {
            flag[2] = 0; break;
        }
        for (j = 1; j <= t[i] - 1; j++) f[j] = false;
        for (auto u : adj[i]) {
            f[t[u]] = true;
            if (t[u] == t[i]) {
                flag[2] = 0; break;
            }
        }
        if (flag[2] == 0) break;
        for (j = 1; j <= t[i] - 1; j++) {
            if (f[j] == false) {
                flag[2] = 0; break;
            }
        }
        if (flag[2] == 0) break;
    }

    if (flag[2] == 0) {
        cout << -1;
        return 0;
    }

    sort(sn.begin(), sn.end());
    for (auto u : sn) {
        cout << u[1] << ' ';
    }

    return 0;
}