#include <bits/stdc++.h>
using namespace std;

#define endl "\n"
#define ll long long
#define INF (ll)1e18
#define maxn 1000010
#define pb push_back

ll i, i1, j, k, k1, t, n, m, res, flag[10], a[maxn], fc, p, e, mod;

ll fxp(ll b, ll e) {
    if (fc == 0) return 0;
    ll k, res;
    if (e == 0) return 1;
    if (e % 2 == 0) {
        k = fxp(b, e / 2);
        res = k * k;
        if (res >= mod) {
            flag[0] = 1; res %= mod;
        }
        return res;
    } else {
        k = fxp(b, e - 1);
        res = b * k;
        if (res >= mod) {
            flag[0] = 1; res %= mod;
        }
        return res;
    }
}

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);

    //ifstream cin("input.txt");
    //ofstream cout("output.txt");

    mod = 1000000007;
    cin >> t;
    while (t--) {
        flag[0] = 0;
        cin >> n >> p;
        for (i = 0; i < n; i++) {
            cin >> a[i];
        }
        sort(a, a + n); reverse(a, a + n);
        fc = 1;
        for (i = 1; i < n; i++) {
            fc *= fxp(p, a[i - 1] - a[i]); fc %= mod;
            if (flag[0] == 0) {
                if (fc == 0) fc = 1;
                else fc--;
            } else {
                fc = (fc - 1 + mod) % mod;
            }
        }
        fc *= fxp(p, a[n - 1]); fc %= mod;

        cout << fc << endl;
    }

    return 0;
}