#define Edg int M=0,fst[SZ],vb[SZ],nxt[SZ];void ad_de(int a,int b){++M;nxt[M]=fst[a];fst[a]=M;vb[M]=b;}void adde(int a,int b){ad_de(a,b);ad_de(b,a);}
#define Edgc int M=0,fst[SZ],vb[SZ],nxt[SZ],vc[SZ];void ad_de(int a,int b,int c){++M;nxt[M]=fst[a];fst[a]=M;vb[M]=b;vc[M]=c;}void adde(int a,int b,int c){ad_de(a,b,c);ad_de(b,a,c);}
#define es(x,e) (int e=fst[x];e;e=nxt[e])
#define esb(x,e,b) (int e=fst[x],b=vb[e];e;e=nxt[e],b=vb[e])
#define SZ 666666
int T,n,m; ll a[SZ];
vector<pii> bt[SZ];
ll f[SZ][2];
void sol() {
	cin>>n>>m;
	for(int i=1;i<=n;++i) cin>>a[i];
	sort(a+1,a+1+n);
	a[0]=-1e18; a[n+1]=1e18;
	for(int i=0;i<=n+1;++i) bt[i].clear();
	for(int i=1;i<=m;++i) {
		int l,r;
		cin>>l>>r;
		int u=lower_bound(a,a+n+1,(ll)l)-a;
		if(a[u]<=r) continue;
		bt[u].pb(pii(l,r));
	}
	for(int i=1;i<=n+1;++i) {
		auto&v=bt[i];
		sort(v.begin(),v.end());
		int m=v.size();
		ll L=a[i-1],R=a[i];
		for(int o=0;o<2;++o) {
			f[i][o]=8e18;
			for(int p=0;p<2;++p) {
				int c1=o+1,c2=(!p)+1;
				ll mi=R;
				for(int j=m-1;j>=0;--j) {
					f[i][o]=min(f[i][o],(R-mi)*c1+(v[j].fi-L)*c2+f[i-1][p]);
					mi=min(mi,(ll)v[j].se);
				}
				f[i][o]=min(f[i][o],(R-mi)*c1+f[i-1][p]);
			}
		}
	}
	ll ans=f[n+1][1];
	cout<<ans<<"\n";
}
void main()
{
#ifdef ONLINE_JUDGE //don't mix cin/scanf, cout/printf!
	cin.tie(0); cout.tie(0);
	ios::sync_with_stdio(0);
#endif
	cin>>T;
	while(T--) sol();
}


