const int MOD = 998244353;

void add(int &a, int b) {
	a += b;
	if (a >= MOD) {
		a -= MOD;
	}
}

int mult(int a, int b) {
	return (a * 1ll * b) % MOD;
}

int b_pow(int a, int b) {
	if (!b) {
		return 1;
	}
	if (b & 1) {
		return mult(a, b_pow(a, b - 1));
	}
	return b_pow(mult(a, a), b >> 1);
}

map<int, int> f, rf;

void calc(int n) {
	if (f.find(n) != f.end()) {
		return;
	}
	auto it = --f.upper_bound(n);
	int pos = it->F, val = it->S;
	for (int i = pos + 1; i <= n; i++) {
		val = mult(val, i);
		f[i] = val;
	}
	return;
}

void calc_rev(int n) {
	if (rf.find(n) != rf.end()) {
		return;
	}	
	calc(n);
	rf[n] = b_pow(f[n], MOD - 2);
}

int C(int n, int k) {
	calc(n);
	calc_rev(n - k);
	calc_rev(k);
	return mult(f[n], mult(rf[k], rf[n - k]));
}

int get(int len, int need) {
	return C(len + need - 1, need);	
}

