		int n;
	cin >> n;
	vector<int> srt;
	vector<int> l(n + 1), r(n + 1);
	int all = 1;
	for (int i = 1; i <= n; i++) {
		cin >> l[n - i + 1] >> r[n - i + 1];
	}
	for (int i = 1; i <= n; i++) {
		all = mult(all, r[i] - l[i] + 1);
	}
	int prv = 0;
	for (int i = 1; i <= n; i++) {
		l[i] = max(prv, l[i]);
		srt.pb(l[i]);
		srt.pb(r[i] + 1);
		prv = max(prv, l[i]);
	}
	sort(all(srt));
	srt.resize(unique(all(srt)) - srt.begin());
	int sz = SZ(srt);
	vector<T> arr(sz);
	unordered_map<int, int> mp;
	for (int i = 0; i < sz; i++) {
		mp[srt[i]] = i;
		arr[i].coord = srt[i];
	}
	for (int i = 1; i <= n; i++) {
		arr[mp[l[i]]].mx_r = max(arr[mp[l[i]]].mx_r, i);
		arr[mp[r[i] + 1]].mx_l = max(arr[mp[r[i] + 1]].mx_l, i);
	}
	for (int i = 1; i < sz; i++) {
		arr[i].mx_r = max(arr[i].mx_r, arr[i - 1].mx_r);
		arr[i].mx_l = max(arr[i].mx_l, arr[i - 1].mx_l);
	}
	dp[0][1][0] = 1;
	for (int i = 0; i < sz; i++) {
		for (int l = 1; l <= n; l++) {
			for (int r = l - 1; r <= n; r++) {
				if (dp[i][l][r] == 0) {
					continue;
				}
				for (int take = 0; take <= r - l + 1; take++) {
					int cnt;
					if (take == 0) {
						cnt = 1;
					} 
					else {
						cnt = get(arr[i].coord - arr[i - 1].coord, take);
					}
					if (arr[i].mx_l < l + take) {
						add(dp[i + 1][l + take][arr[i].mx_r], mult(dp[i][l][r], cnt));
					}
				}		
			}
		}
	}
	int sum = 0;
	for (int i = 0; i <= sz; i++) {
		add(sum, dp[i][n + 1][n]);
	}
