import java.util.*; public class Main {public static void main(String[] args) {Scanner scan = new Scanner(System.in); int n1 = scan.nextInt(); int n2 = scan.nextInt(); int k1 = scan.nextInt(); int k2 = scan.nextInt(); int[][][][] dp = new int[n1+1][n2+1][k1+1][k2+1]; dp[0][0][0][0] = 1; for(int i = 0; i < dp.length; i++) { for(int j = 0; j < dp[i].length; j++) { for(int k = 0; k < dp[i][j].length; k++) {for(int l = 0; l < dp[i][j][k].length; l++){ if(k != k1 && i != n1){ dp[i+1][j][k+1][0] += dp[i][j][k][l]; dp[i+1][j][k+1][0] %= 100000000;} if(j != n2 && l != k2) {dp[i][j+1][0][l+1] += dp[i][j][k][l]; dp[i][j+1][0][l+1] %= 100000000;}}}}}int ret = 0; for(int[] k: dp[n1][n2]) {for(int l: k) {ret += l; ret %= 100000000;}}System.out.println(ret);}}