#define FROGS

#include <cstdio>
#include <algorithm>
#include <utility>
#include <vector>
#include <set>
#include <map>
#include <iostream>
#include <string>
#include <iomanip>
#include <string.h>
#include <numeric>
using namespace std;
typedef long long ll;

int n, k, d[402][402][52], a[402], s[402];
int main()
{
    //freopen("in.txt", "r", stdin);
    //freopen("out.txt", "w", stdout);
    scanf("%d %d", &n, &k);
    for (int i=0;i<n;++i) scanf("%d", &a[i]), s[i+1]=s[i]+a[i];
    /*{
        n=400; k=50;
        for (int i=0;i<n;++i) s[i+1]=rand()-RAND_MAX/2;
    }*/
    for (int l=0;l<=n;++l) for (int r=0;r<=n;++r) for (int k=0;k<=::k;++k) d[l][r][k]=-1e9;
    for (int l=0;l<n;++l) for (int r=l+1;r<n;++r) d[l][r][1]=0;
    int rs=-1e9;
    for (int m=1;m<n;++m) for (int r=m+1;r<=n;++r) for (int k=2;k<=::k;++k)
    {
        int bst=-1e9; int s2=s[r]-s[m];
        for (int l=0;l<m;++l)
        {
            int df=s[m]-s[l]-s2;
            if (df<0) df=-df;
            df+=d[l][m][k-1];
            if (bst<df) bst=df;
        }
        d[m][r][k]=bst;
        if (k==::k) rs=max(rs, bst);
    }
    cout<<rs<<endl;
    return 0;
}