#define FROGS

#include <cstdio>
#include <algorithm>
#include <utility>
#include <vector>
#include <set>
#include <map>
#include <iostream>
#include <string>
#include <iomanip>
#include <string.h>
#include <numeric>
using namespace std;
typedef long long ll;

const int M=1111*1111;
int n, a[M], d[M], f[M];
int main()
{
    scanf("%d\n", &n);
    int m=1<<(n+1);
    for (int i=2;i<m;++i) scanf("%d", &a[i]);
    for (int i=m/2-1;i;--i)
    {
        int l=i*2, r=l+1;
        f[i]=max(f[l]+a[l], f[r]+a[r]);
        d[i]=d[l]+d[r]+f[i]+f[i]-f[l]-a[l]-f[r]-a[r];
    }
    printf("%d\n", d[1]);
    return 0;
}